import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./PostDeleteModal.css";
import blackX from "../img/X_black.png";

function PostDeleteModal({ onDeleteConfirm }) {
  const navigate = useNavigate();
  const [postPassword, setPostPassword] = useState("");

  const handleDelete = async (e) => {
    e.preventDefault(); // 폼 제출 기본 동작 방지

    // 서버에 데이터 전송
    const response = await axios.delete("/api/posts/{postId}", postPassword);

    try {
      await onDeleteConfirm(postPassword); // 삭제 요청을 처리하는 로직 호출 (비밀번호 같이 전달)
      navigate("/"); // 삭제 후 메인 페이지로 이동
    } catch (error) {
      console.error("Error deleting post:", error);
      // 필요에 따라 에러 메시지 표시 로직 추가 가능
    }
  };

  const onClose = () => {
    navigate("/");
  };

  return (
    <div className="postDeleteModalD">
      <div className="headerD">
        <img className="XD" src={blackX} alt="X" onClick={onClose} />
        <div id="contentHeaderD">추억 삭제</div>
      </div>

      <form>
        <div className="deleteContentD">
          <div className="contentHeaderD">삭제 권한 인증</div>
          <input
            className="passwordD"
            type="password"
            value={postPassword}
            onChange={(e) => setPostPassword(e.target.value)}
            placeholder="비밀번호를 입력해주세요"
          />
        </div>
        <div>
          <button
            className="deleteButtonD"
            type="submit"
            onSubmit={handleDelete}
          >
            삭제하기
          </button>
        </div>
      </form>
    </div>
  );
}
export default PostDeleteModal;
