import React, { useState, useRef } from "react"; // + useEffect
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./PostModifyModal.css";
import calender from "../img/calender.png";
import whiteX from "../img/X_white.png";
import blackX from "../img/X_black.png";

function PostModifyModal({
  postId,
  initalData = {},
  onClose,
  onSuccess,
  onFailure,
}) {
  const navigate = useNavigate();

  // 상태 초기화 (initialData로부터 초기값 설정)
  const [postNickname, setPostNickname] = useState(initalData.nickname || "");
  const [postTitle, setPostTitle] = useState(initalData.title || "");
  const [postImage, setPostImage] = useState(null);
  const [postContent, setPostContent] = useState(initalData.content || "");
  const [postTag, setPostTag] = useState(initalData.tags || []);
  const [postLocation, setPostLocation] = useState(initalData.location || "");
  const [postMoment, setPostMoment] = useState(initalData.moment || "");
  const [postPassword, setPostPassword] = useState(""); // 수정 시 입력해야 하는 password

  // 태그 핸들러
  const handleTagInput = (e) => {
    const value = e.target.value;
    if (value.includes(" ")) return; // 띄어쓰기 방지
    setPostTag(value); // 태그 입력 값 설정
  };
  const handleTagKeyDown = (e) => {
    if (e.key === "Enter" && postTag.trim() !== "") {
      if (postTag.length >= 10) {
        alert("태그는 최대 10개까지 추가할 수 있습니다.");
        return;
      }
      setPostTag([...postTag, postTag.trim()]); // 태그 추가
      setPostTag(""); // 입력 값 초기화
    }
  };
  const removeTag = (indexToRemove) => {
    setPostTag(postTag.filter((_, index) => index !== indexToRemove)); // 태그 삭제
  };

  // moment 핸들러
  const dateInputRef = useRef(null);
  const openDatePicker = () => {
    if (dateInputRef.current) {
      dateInputRef.current.showPicker();
    }
  };

  // enter로 폼 제출 방지 (textarea 제외)
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
    }
  };

  // // useEffect로 initialData가 변경될 때 상태를 업데이트
  // useEffect(() => {
  //   setPostNickname(initalData.nickname || "");
  //   setPostTitle(initalData.title || "");
  //   setPostContent(initalData.content || "");
  //   setPostTag(initalData.tags || []);
  //   setPostLocation(initalData.location || "");
  //   setPostMoment(initalData.moment || "");
  // }, [initalData]);

  // 수정 제출
  const handleSubmit = async (e) => {
    e.preventDefault();

    // try {
    // // 1. 이미지 파일을 서버로 업로드 (파일이 선택된 경우)
    // let imageUrl = initialData.imageUrl; // 기본 이미지 URL

    // if (postImage) {
    //   const imageData = new FormData();
    //   imageData.append("image", postImage);

    //   const imageUploadResponse = await axios.post("/api/image", imageData);
    //   imageUrl = imageUploadResponse.data.imageUrl;
    // }

    // 2. 업로드된 이미지의 URL과 나머지 데이터를 서버로 전송
    const formData = new FormData();
    formData.append("nickname", postNickname);
    formData.append("title", postTitle);
    formData.append("imageUrl", postImage); // 이미지 URL 필드에 업로드된 이미지 URL 추가
    formData.append("content", postContent);
    formData.append("tags", JSON.stringify(postTag)); // 태그 배열을 JSON 문자열로 변환하여 추가
    formData.append("location", postLocation);
    formData.append("moment", postMoment);
    formData.append("postPassword", postPassword);

    try {
      // 서버에 데이터 전송
      const response = await axios.put(`/api/posts/${postId}`, formData);

      if (response.status === 200) {
        alert("게시글이 성공적으로 수정되었습니다.");
        onClose(); // 수정 후 모달 닫기
        navigate(`/`); // 상세 페이지로 이동
      } else if (response.status === 400) {
        alert("잘못된 요청입니다.");
      } else if (response.status === 401) {
        alert("비밀번호가 틀렸습니다.");
      } else {
        // response.status===404
        alert("존재하지 않습니다.");
      }
    } catch (error) {
      alert("게시글 수정 중 오류가 발생했습니다. 다시 시도해주세요.");
      console.error("게시글 수정 중 오류 발생:", error);
    }
  };

  // const handleClose = () => {
  //   onClose(); // 모달 닫기
  //   navigate(`/`); // 상세 페이지로 이동
  // };

  return (
    <div className="container">
      <div className="contentHeader">
        <img className="blackX" src={blackX} alt="X" onClick={onClose} />
        <div id="contentHeader">추억 수정</div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="leftForm">
          <div className="noEnter" onKeyDown={handleKeyDown}>
            <div className="nickname">
              <div className="labelFirst">
                <label id="nicknameLabel" htmlFor="nickname">
                  닉네임
                </label>
              </div>
              <input
                className="input"
                id="nickname"
                name="nickname"
                value={postNickname}
                placeholder="닉네임을 입력해주세요"
                onChange={(e) => setPostNickname(e.target.value)}
                required
              />
            </div>

            <div className="title">
              <div className="label">
                <label htmlFor="title">제목</label>
              </div>
              <input
                className="input"
                id="title"
                name="title"
                value={postTitle}
                placeholder="제목을 입력해주세요"
                onChange={(e) => setPostTitle(e.target.value)}
                required
              />
            </div>

            <div className="image">
              <div className="label">
                <label htmlFor="imageUrl">이미지</label>
              </div>
              <div className="buttonMade">
                <input
                  className="input inputFile"
                  id="imageUrl"
                  name="imageUrl"
                  placeholder="파일을 선택해주세요"
                  // value={postImage ? postImage.name : initialData.imageUrl}
                  readOnly
                />
                <label htmlFor="fileInput" className="fileButtonLabel">
                  파일 선택
                </label>
                <input
                  type="file"
                  id="fileInput"
                  name="imageUrl"
                  className="fileButton"
                  onChange={(e) => setPostImage(e.target.files[0])}
                />
              </div>
            </div>
          </div>

          <div className="content">
            <div className="label">
              <label htmlFor="content">본문</label>
            </div>
            <textarea
              className="textarea"
              id="content"
              name="content"
              value={postContent}
              rows="5"
              wrap="hard"
              placeholder="본문 내용을 입력해주세요"
              onChange={(e) => setPostContent(e.target.value)}
              required
            />
          </div>
        </div>

        <div className="line"></div>

        <div className="rightForm" onKeyDown={handleKeyDown}>
          <div className="tag">
            <div className="labelFirst">
              <label htmlFor="tag">태그</label>
            </div>
            <input
              className="input"
              id="tag"
              name="tag"
              value={postTag}
              placeholder="태그를 입력해주세요"
              onChange={handleTagInput}
              onKeyDown={handleTagKeyDown}
            />
            <div className="tagList">
              {postTag.map((tag, index) => (
                <div key={index} className="tagItem">
                  #{tag}{" "}
                  <span className="removeTag" onClick={() => removeTag(index)}>
                    <img src={whiteX} alt="X" />
                  </span>
                </div>
              ))}
            </div>
          </div>

          <div className="location">
            <div className="label">
              <label htmlFor="location">장소</label>
            </div>
            <input
              className="input"
              id="location"
              name="location"
              value={postLocation}
              placeholder="장소를 입력해주세요"
              onChange={(e) => {
                setPostLocation(e.target.value);
              }}
              required
            />
          </div>

          <div className="moment">
            <div className="label">
              <label htmlFor="moment">추억의 순간</label>
            </div>
            <div className="dateWrapper">
              <input
                className="input"
                id="moment"
                name="moment"
                value={postMoment}
                type="datetime-local"
                onChange={(e) => setPostMoment(e.target.value)}
                ref={dateInputRef}
              />
              <img
                src={calender}
                alt="calenderIcon"
                className="calenderIcon"
                onClick={openDatePicker}
              />
            </div>
          </div>

          <div id="protectedForm">
            <div className="password">
              <div className="label">
                <label htmlFor="passwordInput">수정 권한 인증</label>
              </div>
              <input
                className="input"
                id="passwordInput"
                value={postPassword}
                name="postPassword"
                type="password"
                placeholder="비밀번호를 입력해주세요"
                onChange={(e) => setPostPassword(e.target.value)}
                required
              />
            </div>
          </div>
        </div>

        <button className="modifyButton" type="submit">
          수정하기
        </button>
      </form>
    </div>
  );
}

export default PostModifyModal;
