import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import PostForm from "../components/PostForm";
import "./PostCreatePage.css";
import logo from "../img/logo.jpg";

function PostCreatePage(onSuccess, onFailure) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const navigate = useNavigate();

  const handlePostCreationSuccess = (PostId) => {
    setModalMessage("추억 만들기 성공!");
    setIsModalOpen(true);

    setTimeout(() => {
      navigate(`/`); // 추억 상세 페이지로 이동
    }, 2000); // 2초 후에 페이지 전환
  };

  const handlePostCreationFailure = (message) => {
    setModalMessage(message);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // submit 핸들러
  const handleSubmit = async (e) => {
    // e.preventDefault();

    // try {
    // 1. 이미지 파일을 서버로 업로드
    const imageData = new FormData();
    imageData.append("image", postImage);

    const imageUploadResponse = await axios.post("/api/image", imageData); // 명세서에 따른 올바른 경로 사용
    const imageUrl = imageUploadResponse.data.imageUrl;

    // 2. 업로드된 이미지의 URL과 나머지 데이터를 서버로 전송
    const formData = new FormData();

    formData.append("nickname", postNickname);
    formData.append("title", postTitle);
    formData.append("imageUrl", imageUrl); // 이미지 URL 필드에 업로드된 이미지 URL 추가
    formData.append("content", postContent);
    formData.append("tags", JSON.stringify(postTag)); // 태그 배열을 JSON 문자열로 변환하여 추가
    formData.append("location", postLocation);
    formData.append("moment", postMoment);
    formData.append("postPassword", postPassword);
    // formData.append("groupPassword", groupPassword);

    try {
      // 서버에 데이터 전송
      const response = await axios.post(
        "/api/groups/{groupId}/posts",
        formData
      );
      if (response.status === 200) {
        onSuccess(response.data.id); // 생성된 그룹 ID 전달
        // 등록한 게시글 상세 페이지(임시 = /)로 이동
        navigate("/"); //  -> /를 등록한 게시글 상세 페이지로 이동하게끔 수정 필요
      } else if (response.status === 400) {
        onFailure("잘못된 요청입니다.");
      } else if (response.status === 401) {
        onFailure("비밀번호가 틀렸습니다.");
      }
    } catch (error) {
      onFailure("게시글 생성 중 오류가 발생했습니다. 다시 시도해주세요.");
      console.error("게시글 생성 중 오류 발생:", error);
    }
  };

  return (
    <div className="postCreatePageC">
      <div className="headerC">
        <div className="headerLogoC">
          <img id="logoC" src={logo} alt="로고" />
        </div>
      </div>
      <div className="containerC">
        <div className="contentHeaderC">
          <div id="contentHeaderC">추억 올리기</div>
        </div>

        <PostForm
          onSuccess={handlePostCreationSuccess}
          onFailure={handlePostCreationFailure}
        />

        <button className="postButtonC" type="submit" onSubmit={handleSubmit}>
          올리기
        </button>
      </div>
    </div>
  );
}

export default PostCreatePage;
