import React, { useState } from "react";
import "./PostDeleteModal.css";
import blackX from "../img/X_black.png";

function PostDeleteModal(onDeleteConfirm, onClose) {
  const [postPassword, setPostPassword] = useState("");

  const handleDelete = () => {
    onDeleteConfirm(postPassword); // 입력된 비밀번호를 함께 전달
  };

  return (
    <div>
      <div className="header">
        <img className="X" src={blackX} alt="X" onClick={onClose} />
        <div id="contentHeader">추억 삭제</div>
      </div>

      <form>
        <div className="deleteContent">
          <div className="contentHeader">삭제 권한 인증</div>
          <input
            className="password"
            type="password"
            value={postPassword}
            onChange={(e) => setPostPassword(e.target.value)}
            placeholder="비밀번호를 입력해주세요"
          />
        </div>
        <div>
          <button className="deleteButton" type="submit" onClick={handleDelete}>
            삭제하기
          </button>
        </div>
      </form>
    </div>
  );
}

export default PostDeleteModal;
